package com.lyx.silversea.common.respon;import com.alibaba.fastjson.JSONObject;import org.apache.commons.lang3.builder.ReflectionToStringBuilder;import java.io.Serializable;/** * * 统一返回格式 构建 * * @author liuyanxin @gvSoft * @create 2018-12-25 16:33 **/public class AppResponse<T> implements Serializable {    /**     * 返回编码     */    private int code;    /**     * 消息描述     */    private String msg;    /**     * 返回内容     */    private T data;    public int getCode() {        return code;    }    public String getMsg() {        return msg;    }    public T getData() {        return data;    }    private AppResponse(){    }    public AppResponse(int code, String msg, T data) {        this.code = code;        this.msg = msg;        this.data = data;    }    /**     * 构建操作成功返回体     *     * @return     */    public static AppResponse success(){        return builder().code(AppResponseStatus.SUCCESS).build();    }    /**     * 构建操作成功返回体     *     * @param msg     * @return     */    public static AppResponse success(String msg) {        return builder().code(AppResponseStatus.SUCCESS).msg(msg).build();    }    /**     * 构建操作成功返回体     * @param msg     * @param data     * @return     */    public static <T> AppResponse success(String msg, T data) {        return builder().code(AppResponseStatus.SUCCESS).msg(msg).data(data).build();    }    public static <T> AppResponseBuilder builder(){        return new AppResponseBuilder<T>();    }    @Override    public String toString() {        return ReflectionToStringBuilder.toString(this);    }    public String toJson() {        return JSONObject.toJSONString(this);    }    public static class AppResponseBuilder<T> {        private int code;        private String msg;        private T data;        public AppResponse build(){            AppResponse appResponse = new AppResponse<>(this.code,this.msg,this.data);            return appResponse;        }        public AppResponseBuilder code(AppResponseStatus status) {            this.code = status.code;            return this;        }        public AppResponseBuilder msg(String msg) {            this.msg = msg;            return this;        }        public AppResponseBuilder data (T data) {            this.data = data;            return this;        }    }}