package com.lyx.silversea.service.system.impl;import com.lyx.silversea.dao.system.UserDao;import com.lyx.silversea.domain.system.User;import com.lyx.silversea.service.system.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.Map;/** * @author liuyanxin @gvSoft * @create 2018-12-21 16:43 **/@Servicepublic class UserImpl implements UserService {    @Autowired    private UserDao userDao;    @Override    public User loginUserInfo(String loginname, String loginpwd) {        Map<String,String> map = new HashMap<>();        map.put("loginname",loginname);        map.put("password",loginpwd);        return userDao.userLoginInfo(map);    }}